mapper.select:
SELECT id, userId, title, author, DATE_FORMAT(date, '%Y-%m-%d') as date, content, likes
FROM reviews
WHERE id = #{id}

SELECT id, userId, title, author, DATE_FORMAT(date, '%Y-%m-%d') as date, content
FROM reviews
WHERE userId = #{userId} AND title = #{title}

SELECT r.id, r.userId, r.title, r.author, DATE_FORMAT(r.date, '%Y-%m-%d') AS "date", r.content, r.likes, u.PROFILEIMGPATH,
CASE WHEN l.userid IS NOT NULL THEN true ELSE false END AS liked
FROM reviews r
JOIN users u ON r.userId = u.id
LEFT JOIN likes l ON r.id = l.reviewId AND l.userid = #{userId}
WHERE title = #{title}
ORDER BY r.id DESC
LIMIT #{pageable.pageSize} OFFSET #{pageable.offset};

SELECT r.id, r.userId, r.title, r.author, DATE_FORMAT(r.date, '%Y-%m-%d') AS "date", r.content, r.likes, u.PROFILEIMGPATH,
CASE WHEN l.userid IS NOT NULL THEN true ELSE false END AS liked
FROM reviews r
JOIN users u ON r.userId = u.id
LEFT JOIN likes l ON r.id = l.reviewId AND l.userid = #{userId}
WHERE title = #{title}
ORDER BY r.likes DESC
LIMIT #{pageable.pageSize} OFFSET #{pageable.offset};

SELECT COUNT(*)
FROM reviews
WHERE title = #{title}

SELECT COUNT(*)
FROM reviews
WHERE userId = #{userId}

SELECT title, DATE_FORMAT(date, '%Y-%m-%d') as date
FROM reviews
WHERE userId = #{userId}
ORDER BY id DESC
LIMIT #{pageable.pageSize} OFFSET #{pageable.offset};

mapper.update:
UPDATE reviews
SET likes = likes + 1
WHERE id = #{id}

UPDATE reviews
SET likes = likes - 1
WHERE id = #{id}

UPDATE reviews
SET content = #{content}
WHERE id = #{id}
