mapper.select:
SELECT id, profileImgPath, nickname, email, password, signUpType, FORMATDATETIME(signUpDate, 'yyyy-MM-dd') as signUpDate
FROM users
WHERE id = #{id}

mapper.update:
UPDATE users
nickname = #{updatedUser.nickname}, password = #{updatedUser.password},
WHERE id = #{id}

mapper.select:
SELECT id, nickname, email, password
FROM users
WHERE email = #{email}
